INSERT INTO `yy_fault_manylanguage` (`fault_id`, `lang_id`, `fault_num`, `fault_name`, `fault_cause`, `fault_countermeasure`) VALUES
(19, 1, 'Q1', 'Black Speck', '<span id="tran_0" class="copied" style="color:#666666;font-family:Tahoma, Arial;line-height:24px;background-color:rgba(255, 255, 255, 0.8);">BS is a molding defect where (a) black point(s) or\r\nline(s) appears on the surface of a molded product. It is caused by\r\ndecomposition resin or Contamination.</span><span id="tran_1" class="copied" style="color:#666666;font-family:Tahoma, Arial;line-height:24px;background-color:rgba(255, 255, 255, 0.8);"></span>', '<span id="tran_0" class="copied" style="color:#666666;font-family:Tahoma, Arial;line-height:24px;background-color:rgba(255, 255, 255, 0.8);"> \r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	1)&nbsp; To sufficiently purge till no specks are found\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	2)&nbsp; To lower resin temperature\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	3)&nbsp; To shorten residence time\r\n</p>\r\n4) &nbsp;Another\r\nresin or a metal is contaminated</span><span id="tran_3" class="copied" style="color:#666666;font-family:Tahoma, Arial;line-height:24px;background-color:rgba(255, 255, 255, 0.8);"></span><br />'),
(19, 2, 'Q1', 'BS( 黑点 )', '<span style="color:#666666;font-family:微軟正黑體;font-size:13px;line-height:25px;">成型品表面出现的黑点现象。这是由于在机筒内已分解的树脂或被污染的杂物等混入到成型品中而造成的。<span style="color:#666666;font-family:微軟正黑體;font-size:13px;line-height:25px;"></span></span>', '<span style="color:#666666;font-family:微軟正黑體;font-size:13px;line-height:25px;"> \r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	1)&nbsp; 首先请充分清洗直至黑点不再出现\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	2)&nbsp; 尝试降低树脂温度\r\n</p>\r\n<p class="MsoNormal" style="margin-left:18.0pt;text-indent:-18.0pt;">\r\n	3)&nbsp; 缩短滞留时间\r\n</p>\r\n4) &nbsp;查看是否有他树脂或金属混入</span><span style="color:#666666;font-family:微軟正黑體;font-size:13px;line-height:25px;"></span>'),
(20, 1, 'Q2', 'Pit marks', 'Pit marks are small points on the surface of molding\r\npieces. It is caused by low injection speed.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp; <!--[endif]-->try to raise the setting of holding pressure little by little<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp; <!--[endif]-->Easily applicable conditions for pressure holding: <b><u></u></b> \r\n</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;1. To\r\nraise resin temperature<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;2. To\r\nraise mold temperature<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;3. To\r\nraise injection speed<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;4. To\r\nexpand gate size<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;5. To\r\nadjust wall thickness<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;6. To\r\nuse a material of good flowability<br />'),
(20, 2, 'Q2', '麻点', '成型品表面形成小凹坑的现象。注射速度较慢时易于发生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp; <!--[endif]-->尝试逐渐提高保压设定值<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp; <!--[endif]-->易于施加保压的条件<b><u></u></b> \r\n</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;1.提高树脂温度<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;2.提高模具温度<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;3.提高注射速度<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;4.扩大浇口<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;5.调整厚度（1t～2t即可）<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;6.使用具有良好流动性的材料'),
(23, 1, 'Q3', 'Weld line', 'Weld means a joining part of resin flowing in the\r\ncavity during injection molding. Weld line means a line at the weld', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;To raise the resin\r\ntemperature at the weld part<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2) &nbsp;To raise holding\r\npressure<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3) &nbsp;Check if the thickness\r\nand size to vent gas efficiently are secured, and if the vent is fouled with MD<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<span style="line-height:1.5;"><span>4) &nbsp;</span>Check\r\ngate is\r\nsufficient or\r\nnot, to keep facilitating resin flow and increasing effective holding pressure.</span> \r\n</p>'),
(23, 2, 'Q3', '熔合纹', '成型品上有开孔时必然会产生熔合纹。熔合纹是指该熔合部分实际上看起来是一条线的现象。模具温度较低时容易出现这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;<!--[endif]-->提高熔合部分的树脂温度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;尝试提高保压<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;检查排气口的厚度和大小确保排气通畅、是否受到模垢的污染等<b><u></u></b> \r\n</p>\r\n<span>4) &nbsp;</span>检查浇口的尺寸是否不够，以确保树脂流动更加通畅'),
(24, 1, 'Q4', 'Gas burn', 'Gas burn is burning mark on the surface of test piece.\r\nGas burn occurs when entrapped gas is rapidly compressed and becomes high\r\ntemperature at a resin filling end.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;To reduce injection speed<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To improve gas venting:<b><u></u></b> \r\n</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;1. If\r\nthe vent is not plugged by MD or the like<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;2. To intensify vent capacity<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;3. If\r\nthe vent is at a proper position'),
(25, 2, 'Q5', '玻纤析出', '成型品充填末端烧焦的现象。排气不良时易于产生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;减慢注射速度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2) &nbsp;改善排气状况<b><u></u></b> \r\n</p>\r\n1.是否被模垢堵塞?<br />\r\n2. 增强排气口的能力<br />\r\n3. 是否位于适当的位置?'),
(24, 2, 'Q4', '气体烧焦', '成型品充填末端烧焦的现象。排气不良时易于产生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;减慢注射速度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2) &nbsp;<!--[endif]-->改善排气状况<b><u></u></b> \r\n</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;1.是否被模垢堵塞?<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;2. 增强排气口的能力<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;3. 是否位于适当的位置?'),
(25, 1, 'Q5', 'Glass marks', 'Glass marks are generated from the same cause of Haze. Low holding pressure or low\r\nmold temperature is prone to this phenomenon.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1) &nbsp;To raise holding pressure\r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To raise mold temperature\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     raise cylinder temperature (in particular, nozzle temperature)\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To expand the gate size\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     increase injection speed\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		<span>To use a grade of high flowability</span><br />\r\n	</li>\r\n</ol>\r\n<br />'),
(26, 1, 'Q6', 'Haze', 'Haze indicates insufficient contact of a molded\r\nproduct to a mold, in other words, transcription failure.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1) &nbsp;To raise holding pressure\r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To raise mold temperature\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     raise cylinder temperature (in particular, nozzle temperature)\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To expand the gate size\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     increase injection speed\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		<span>To use a grade of high flowability</span><br />\r\n	</li>\r\n</ol>\r\n<br />'),
(26, 2, 'Q6', '光泽不好', '成型品表面光泽消失的现象。保压或模具温度较低时易于产生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1) &nbsp;尝试提高保压\r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		提高模具温度\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		提高机筒温度（特别是喷嘴）\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		增大浇口尺寸\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		提高注射速度\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		<span>使用流动性好的材料等级</span> \r\n	</li>\r\n</ol>'),
(27, 1, 'Q7', 'Cold slag', 'Cold Slag is a phenomenon that solidified resin comes\r\ninto the cavity. Cold Slag appear on the surface of mold piece like Haze or\r\nJetting. Otherwise, their countermeasures are not same.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To raise nozzle temperature<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2) &nbsp;<!--[endif]-->To expand slag well<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nretreat the cylinder every cycle'),
(27, 2, 'Q7', '冷料', '喷嘴温度偏低, 令稍稍冷却的树脂突出成型品表面的现象。看起来与雾面或喷射纹相同。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;提高喷嘴温度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;扩大冷料阱（树脂积存处）<b><u></u></b> \r\n</p>\r\n3) &nbsp;使机筒每个周期后退一次'),
(28, 1, 'Q8', 'Jetting', 'Jetting looks like a strand pattern or like haze. It\r\nis caused by much high injection speed or small size gate.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;To expand gate size<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To change gate position<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To reduce injection speed<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;Reduce resin viscosity\r\n</p>\r\n5) &nbsp;To\r\ncheck holding pressure'),
(28, 2, 'Q8', '喷射纹', '注射速度过快时树脂流动前端突出的现象。成型品表面会出现各种花纹。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;尝试增大浇口尺寸<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;尝试更改浇口位置<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3) &nbsp;<!--[endif]-->尝试降低注射温度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->4) &nbsp;<!--[endif]-->降低树脂粘度<b><u></u></b> \r\n</p>\r\n5) &nbsp;检查保压'),
(29, 1, 'Q9', 'Short shot', 'Short shot occurs when the flowability of resin is not\r\nsufficient or when the metering value of resin is too small, then make the\r\nincomplete resin filling.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;<!--[endif]-->To raise flowability<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To check metering value<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\ncheck gas vent'),
(29, 2, 'Q9', '充填不足', '因流动性不足及计量值偏少，令成型品无法完全充填的现象', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;尝试提高流动性<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;检查计量值<b><u></u></b> \r\n</p>\r\n3) &nbsp;检查排气状况'),
(30, 1, 'Q10', 'lver marks', 'Silver mark is a phenomenon that gas or air appears on\r\nthe surface of a molded product. It is caused by decomosit gas, moisuture and\r\nentrainment air.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To check metering condition<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To check resin temperature: Decomposition of resin<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To intensify drying<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;To check if any\r\ncontamination<b><u></u></b> \r\n</p>\r\n5) &nbsp;To\r\ncheck vent'),
(30, 2, 'Q10', '银纹', '浇口附近呈现出细小流痕（树脂流动花纹）的现象。主要原因是带入了水分或气体。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1) &nbsp;检查计量状态<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;检查树脂温度：树脂分解<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;强化干燥<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;检查有无污染物<b><u></u></b> \r\n</p>\r\n5) &nbsp;检查排气口'),
(31, 1, 'Q11', 'Poor transcription of emboss', 'Poor transcription of grain pattern means failure of\r\nclean transcription of a grain pattern to a molded product', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To raise holding pressure<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To modify mold: Expand the gate size and/or runner size<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nchange to a\r\nmaterial of good flow ability if still cannot be solved'),
(31, 2, 'Q11', '压花不均匀', '在使用压花模具时，由于成型品的某些部位与模具没有完全吻合所引起的不均匀的现象。保压或模具温度较低时容易发生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;提高保压力<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;模具的改造: 扩大浇口尺寸和分流道尺寸<b><u></u></b> \r\n</p>\r\n3) &nbsp;如仍无法解决问题改用流动性好的材料'),
(32, 1, 'Q12', 'Slipping', 'Slip mark is a phenomenon that the surface once\r\nsolidified slips by holding pressure or injection pressure and is pressed on\r\nthe mold again to show the pattern on the surface of a molded product.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To adjust injection\r\nspeed<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To adjust mold\r\ntemperature<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To change mold shape<b><u></u></b> \r\n</p>\r\n4) &nbsp;To\r\nchange a substituting grades of material if still cannot be solved'),
(32, 2, 'Q12', '滑痕', '已固化的表面发生移动并在表面留下当时移动的花纹的现象。顶出针或无R转角部容易发生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;调整注射速度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;调整模具温度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;更改模具形状<b><u></u></b> \r\n</p>\r\n<span class="style11">4) &nbsp;无法解决</span>时，改用其他等级的材料'),
(33, 1, 'Q13', 'Flash', 'Flash is a phenomenon where resin enters between the\r\nPL surfaces of a mold to produce an undesirable thin film on a product.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To lower resin pressure<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To check mold clamping\r\nforce<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To accelerate resin\r\nsolidification<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;To check PL surfaces of\r\nmold<b><u></u></b> \r\n</p>\r\n5) &nbsp;In\r\ncase of PPS resin,&nbsp;Limit the thickness of gas vent around 10cm and increase the number \r\ninstead.&nbsp;'),
(33, 2, 'Q13', '飞边', '树脂进入模具分型线（PL）从而使薄膜附着在成型品四周的现象。树脂粘度较低或保压过高时会出现这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;降低树脂压力<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;检查合模力<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;加快树脂固化<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;检查模具PL面<b><u></u></b> \r\n</p>\r\n5) &nbsp;如果是PPS（聚苯硫醚）树脂，排气口的厚度应达到10厘米左右，<span class="style1">同时增加排气口的数量</span>'),
(34, 1, 'Q14', 'Sink mark', 'Sink means a state of product surface drawn down\r\ntoward inside to form a dent along with cooling, solidification and shrinkage\r\nof resin.', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To raise holding\r\npressure<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To lower mold\r\ntemperature<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nreduce wall thickness'),
(34, 2, 'Q14', '凹痕', '成型品表面出现凹陷的现象。厚壁产品容易出现这种现象。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;尝试提高保压<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;尝试降低模具温度<b><u></u></b> \r\n</p>\r\n3) &nbsp;减小厚度'),
(35, 1, 'Q15', 'Gate brush', 'Gate brush is small flow mark near gate. It is common\r\nmark on POM\r\nmold pieces', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nTo smooth flow at the\r\ngate<b><u></u></b> \r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     raise mold temperature,\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     reduce injection speed (in passing gate)\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     expand gate\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		<span style="line-height:1.5;">To\r\nswitch to a grade of better flowability</span> \r\n	</li>\r\n</ol>'),
(35, 2, 'Q15', '须状斑纹', '浇口周边形成独特的胡须状旋涡斑纹的现象。POM树脂容易产生这一问题。', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1) &nbsp;改善浇口处的流动状况<b><u></u></b> \r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		提高模具温度\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		降低注射速度（通过浇口时）\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		增大浇口尺寸\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		<span>改用流动性好的等级</span><br />\r\n	</li>\r\n</ol>\r\n<br />'),
(36, 1, 'Q16', 'Delamination', '<p class="MsoNormal" align="left">\r\n	Delamination\r\nis a phenomenon that surface layer of mold piece is easily delamination, main cause by high shear, foreign material contamination and\r\ntoo\r\nmuch gas mixed in surface layer.\r\n</p>', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1) &nbsp;To reduce shear<b><u></u></b> \r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     raise cylinder temperature (including nozzle),\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     raise mold temperature,\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     reduce injection speed,\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		To\r\n     reduce holding pressure\r\n	</li>\r\n</ol>\r\n2) &nbsp;To\r\nsuppress gas generation'),
(36, 2, 'Q16', '表面剥离', '成型品的表面层与内部层产生分离的现象，主要由于剪切力偏大，混入不同材料，大量气体混入表层', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	1) &nbsp;降低剪切力<b><u></u></b> \r\n</p>\r\n<ol>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		提高机筒温度（包括喷嘴）\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		提高模具温度\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		减慢注射速度\r\n	</li>\r\n	<li class="MsoNormal" style="color:#444444;text-align:left;">\r\n		降低保压\r\n	</li>\r\n</ol>\r\n2) &nbsp;抑制气体'),
(37, 1, 'Q17', 'Blister', '<p class="MsoNormal" align="left">\r\n	Blister\r\nis a phenomenon that surface of mold piece puff up, main cause by air\r\ncatching or too much gas generated from resin.\r\n</p>', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To reduce air catching<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To impede interlayer\r\ndelamination<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nretard gas generation'),
(37, 2, 'Q17', '气泡', '成型品表面拱状鼓起的现象，主要由于卷入空气或树脂中产生大量气体', '<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;减少空气卷入<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:18pt;text-indent:-18pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;使层间难以剥离<b><u></u></b> \r\n</p>\r\n3) &nbsp;抑制气体的产生'),
(39, 1, 'Q18', 'Discoloration', 'Since plastics are chemical compounds, continuous\r\nheating over melting points decomposes or degrades them little by little.\r\nDiscoloration occurs accompanying this.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]--><span class="style11">1) </span><!--[endif]-->To reduce resin\r\ntemperature<span class="style11"><b><u></u></b></span> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To reduce shear in\r\nmetering<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nreduce shear inside mold'),
(39, 2, 'Q18', '变色', '成型品颜色发生变化的现象。原因估计是滞留或树脂分解等。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]--><span class="style11">1) </span>降低树脂<span class="style11">温度</span><span class="style11"><b><u></u></b></span> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;降低计量时的剪切力<b><u></u></b> \r\n</p>\r\n3) &nbsp;降低模具内的剪切力'),
(40, 1, 'Q19', 'Void', '<p class="MsoNormal" align="left">\r\n	"Void"\r\nis a phenomenon that hole is generated in mold piece. The causes of void are\r\nclassified into two.\r\n</p>\r\n<p class="MsoNormal" align="left">\r\n	1. Void by gas: mixing\r\nof a considerable amount of gas\r\n</p>\r\n2.\r\nVacuum\r\nvoid: shrinkage\r\nof resin in a thick part.&nbsp;', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->\r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To compensate resin<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To delay solidification of surface<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To suppress gas generation<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;To facilitate gas venting<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->5)&nbsp;&nbsp;To reduce wall thickness<b><u></u></b> \r\n</p>\r\n6) &nbsp;To\r\nuse a material of the high viscosity type\r\n<p>\r\n	<br />\r\n</p>'),
(40, 2, 'Q19', '空洞', '<span>成型品内部产生空洞的现象。厚壁产品容易产生气体空洞及真空空洞现象。</span>', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	1) <span class="style11">补</span>充树脂<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	2)&nbsp;&nbsp;减慢表面固化<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	3)&nbsp;&nbsp;抑制气体的产生<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	4)&nbsp;&nbsp;改善排气状况<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	5)&nbsp;&nbsp;减小厚度<b><u></u></b> \r\n</p>\r\n<span>6) &nbsp;使用高粘度型材料</span>'),
(41, 1, 'Q20', 'Waving marks', 'Waving marks are wave pattern like marks generated\r\nnear a gate.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To increase injection speed<b><u></u></b> \r\n</p>\r\n2) &nbsp;To delay surface\r\nsolidification'),
(41, 2, 'Q20', '波纹', '浇口附近出现波纹状皱纹的现象。射速过慢时会产生这种现象。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;加快注射速度<b><u></u></b> \r\n</p>\r\n2) &nbsp;减慢表面固化'),
(42, 1, 'Q21', 'Mold Deposit', 'MD is a phenomenon that some component contained in\r\nresin deposits and gradually accumulates on a mold.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To lower resin\r\ntemperature<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To shorten residence\r\ntime<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To confirm /\r\nintensify drying condition<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;To raise mold\r\ntemperature<b><u></u></b> \r\n</p>\r\n5) &nbsp;To\r\ncheck vent'),
(42, 2, 'Q21', 'MD（模垢）', '是指模具内聚集的污物。树脂分解物等在很长时间内逐渐积累，达到严重程度时便会影响形状和外观。POM树脂容易产生这种问题。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;试着降低树脂温度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;缩短滞留时间<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;确认/强化干燥条件<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;提高模具温度<b><u></u></b> \r\n</p>\r\n5) &nbsp;检查排气口'),
(43, 1, 'Q22', 'Stringiness', 'Stringiness is a phenomenon that the sprue tip does\r\nnot cut but extends stringy.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To solidify sprue tip<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To increase mold opening\r\nspeed, and increase mold opening distance<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nchange nozzle'),
(43, 2, 'Q22', '拉丝', '成型品主流道前端与成型机喷嘴之间出现树脂细丝的现象。出现这种现象后，开模时成型品不脱模，因此无法连续成型。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;使主流道前端固化<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;加快开模速度，增大开模量<b><u></u></b> \r\n</p>\r\n3) &nbsp;更改喷嘴'),
(44, 1, 'Q23', 'Crack formation', 'This molding failure is to be cracked when mold is opened. If mold piece is brittle or poor release from mold, it is more easy to generate crack', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;Countermeasures against\r\npoor mold releasability<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;Countermeasures against\r\nembrittlement<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nsuppress crack formation by reducing the load on a molded product by decreasing\r\nmold opening speed and ejection speed'),
(44, 2, 'Q23', '裂纹', '顶出或开模时成型品发生破裂的现象，多数由于脱模不良及成型品偏脆所致。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;实施针对脱模不良的对策<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;针对脆化的对策<b><u></u></b> \r\n</p>\r\n3) &nbsp;减慢开模速度和顶出速度以减轻成型品所承受的负荷'),
(45, 1, 'Q24', 'Gate Cutting Failure', 'This is a phenomenon that a product and the sprue / runner\r\nare not cut off at the gate in a pinpoint gate or the like.', 'To change strength balance'),
(45, 2, 'Q24', '浇口切割不良', '点浇口或潜伏式浇口模具在开模时未能像预期的那样切割浇口部分的现象。', '改变强度平衡'),
(46, 1, 'Q25', 'Gate residue', 'Gate residue means a phenomenon that a gate remains on\r\nthe surface of a molded product.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To advance gate\r\nsolidification<b><u></u></b> \r\n</p>\r\n2) &nbsp;To\r\nchange gate shape'),
(46, 2, 'Q25', '浇口残留', '点浇口或潜伏式浇口模具在开模时未能在预期的位置切割浇口部分，从而在成型品上形成部分残留的现象。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;促进浇口固化<b><u></u></b> \r\n</p>\r\n2) &nbsp;更改模具形状'),
(47, 1, 'Q26', 'Unstable metering', 'This failure is phenomenon that pellet can not be\r\ncarried forward or Metering value is unstable.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To adjust screw rotation\r\nspeed<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To reduce back pressure<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;Cylinder temperature<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;Problem proper to grade<b><u></u></b> \r\n</p>\r\n5) &nbsp;Recycled\r\nmaterial'),
(47, 2, 'Q26', '计量不良', '计量出现偏差或计量时间延长的现象。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;调整螺杆转速<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;降低背压<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;机筒温度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->4)&nbsp;&nbsp;等级固有的问题<b><u></u></b> \r\n</p>\r\n5) &nbsp;回收材料'),
(48, 1, 'Q27', 'Shot to shot variation', 'This failure is that quality of mold product has\r\nvariation according to every shot.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To apply holding\r\npressure sufficiently<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To review mold\r\ntemperature control<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;To stabilize metering<b><u></u></b> \r\n</p>\r\n4) &nbsp;To\r\nimprove gas vent'),
(48, 2, 'Q27', '注射量不稳定', '成型品的重量或尺寸在每次注射时都参差不齐，容易有注射量不稳定的现象。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;充分施加保压<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;检查模温机<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->3)&nbsp;&nbsp;使计量保持稳定<b><u></u></b> \r\n</p>\r\n4) &nbsp;改善排气状况'),
(49, 1, 'Q28', 'Sprue sticking', 'Sprue sticking is a phenomenon that the sprue of a\r\nmolded product can not be separated from the mold.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To open the mold after\r\ncooling and solidifying the sprue sufficiently<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To lower holding\r\npressure<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nimprove mold'),
(49, 2, 'Q28', '主流道粘模', '主流道粘贴并残留在模具固定侧的现象', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;使主流道充分冷却并固化后再开模<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;降低保压<b><u></u></b> \r\n</p>\r\n3) &nbsp;改善模具'),
(50, 1, 'Q29', 'Drooling', 'This phenomenon is that melting resin is leaked from\r\nnozzle of cylinder.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To increase resin\r\nviscosity<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To reduce inner pressure<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nchange nozzle shape'),
(50, 2, 'Q29', '流涎', '树脂从成型机喷嘴漏出的现象。严重时将无法进行连续成型。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;提高树脂粘度<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;降低内压<b><u></u></b> \r\n</p>\r\n3) &nbsp;更改喷嘴形状<br />'),
(51, 1, 'Q30', 'Deformation', 'This is because molding shrinkage becomes partially\r\nununiform due to difference of molding conditions such as pressure and\r\ntemperature, difference of partial wall thickness of molded product,\r\norientation of fillers, and so on.', 'directed\r\nagainst with different shapes\r\ndesign to measures and solved problem'),
(51, 2, 'Q30', '变形', '不均匀的成型收缩是变形问题的原因所在', '须针对不同形状设计因素作出对策'),
(52, 1, 'Q31', 'Poor mold releasability', 'This molding failure is that mold product is stuck in\r\nmold cabity.', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;To suppress grasp of a\r\ncore pin<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;To suppress overfilling\r\nto ribs and bosses<b><u></u></b> \r\n</p>\r\n3) &nbsp;To\r\nimprove mold'),
(52, 2, 'Q31', '脱模不良', '成型品因无法顶出而残留在模腔内的现象。', '<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->1)&nbsp;&nbsp;抑制塑孔栓抱紧<b><u></u></b> \r\n</p>\r\n<p class="MsoNormal" align="left" style="margin-left:24pt;text-indent:-24pt;">\r\n	<!--[if !supportLists]-->2)&nbsp;&nbsp;抑制加强筋或轮毂的过填充<b><u></u></b> \r\n</p>\r\n3) &nbsp;改善模具');

